import React, { useState } from 'react';

import './styles.css';
// import '../../global.css';
import {FiArrowLeft} from 'react-icons/fi'
// import {FiTrash2} from 'react-icons/fi'

import {Link, useHistory } from 'react-router-dom';

import logoimg from '../../assets/logo.svg'

import api from '../../services/api'

export default function NewIncidente() {
    const [title,setTitle] = useState('');
    const [description,setDescription] = useState('');
    const [value,setValue] = useState('');

    cosnt 

    const ongId = localStorage.getitem('ongId');

async function handleNewIncident(e) {
        e.preventDefault();

        const data = {
            title,
            description,
            value,
        };

        try{
            await api.post('incidents', data, {
                headers: {
                    Authorization: ongId,
                }
            })


        } catch(err) {
            alert('Erro. O caso não foi cadastrado. Tente novamente.')
        }
    }
    return (
        <div className="new-incident-container">
        <div className="content">
            <section>
                <img src={logoimg} alt="Be The Hero" />
                <h1>Cadastrar novo caso</h1>
                <p>Descreva o caso detalhamente para encotrar um herói para resolver isso.</p>
                <Link className="back-link" to="/profile"><FiArrowLeft size={18} color="#e02041"/>Voltar para a lista de casos</Link>
            </section>
            <form onSubmit={handleNewIncident}>
                <input 
                    placeholder="Título do caso"
                    value={title} 
                    onChange = {e => setTitle(e.target.value)}
                    />
                <textarea 
                    placeholder="Descrição" 
                    value={description}
                    onChange = {e => setDescription(e.target.value)}
                    />
                <input 
                    placeholder="Valor em Reais" 
                    value={value}
                    onChange = {e => setValue(e.target.value)}
                    />
                <button className="button" type="submit">Cadastrar</button>
            </form>
        </div>
    </div>
    )
}
