import React, {useState} from 'react';

import api from '../../services/api';
import './styles.css';

import {Link, useHistory } from 'react-router-dom';

import {FiArrowLeft} from 'react-icons/fi'

import logoimg from '../../assets/logo.svg'


export default function Register() {
const [name, setName ] = useState('');
const [email, setEmail ] = useState('');
const [whatsapp, setwhatsapp ] = useState('');
const [city, setCity ] = useState('');
const [uf, setUf ] = useState('');

const history = useHistory();

async function handleRegister(event) {
    event.preventDefault();
    const data = ({
        name,
        email,
        whatsapp,
        city,
        uf});
    try {
        const response = await api.post('ongs', data);

        alert(`Seu ID de acesso ${response.data.id}`);

        useHistory().push('/');

    } catch (error){
        
        alert('Erro no cadastro, tente novamente.' + error)
    }
}

    return (
        <div className="register-container">
            <div className="content">
                <section>
                    <img src={logoimg} alt="Be The Hero" />
                    <h1>Cadastro</h1>
                    <p>Faça o seu cadastro, entre na plataforma e ajude pessoas a encontrarem os casos da sua ONG.  </p>
                    <Link className="back-link" to="/"><FiArrowLeft size={18} color="#e02041"/>Não tenho cadastro</Link>
                </section>
                <form onSubmit={handleRegister}>
                    <input
                        placeholder="Nome da ONG"
                        value={name}
                        onChange = {e => setName(e.target.value)}
                    />
                    <input 
                        type="email"
                        placeholder="e-mail"
                        value={email}
                        onChange = {e => setEmail(e.target.value)}
                        />
                    <input 
                        placeholder="WhatsApp"
                        value={whatsapp}
                        onChange = {e => setwhatsapp(e.target.value)}
                     />
                    <div className="input-group">
                        <input
                            placeholder="Cidade"
                            value={city}
                            onChange = {e => setCity(e.target.value)}
                    />
                        <input placeholder="UF"
                        style={{ width: 80 }}
                        value={uf}
                        onChange = {e => setUf(e.target.value)}    
                    />
                    </div>
                    <button className="button" type="submit">Cadastrar</button>
                </form>
            </div>
        </div>
    )
}
